import { useState } from 'react';

import './GuessApp.css';
import compare from './compare';

function GuessApp() {
    const secretWord = 'RECAT';
    const [userInput, setInput] = useState('');
    const [errMessage, setErrorMessage] = useState('');

    const checkInput = () => {
        if (userInput.length !== 5)
            setErrorMessage(`${userInput} was not a valid word`);
        else if (userInput === secretWord)
            setErrorMessage(`${userInput} is the secret word!`);
        else
            setErrorMessage(
                `${userInput} had ${compare(
                    secretWord,
                    userInput
                )} letters in common`
            );

        setInput('');
    };

    return (
        <div className='guess-app'>
            <div className='guess-panel'>
                <div>Please input a 5 letter word:</div>
                <span>
                    <input
                        className='word-input'
                        type='text'
                        onChange={(e) => {
                            setInput(e.target.value);
                        }}
                        value={userInput}
                    ></input>
                </span>
                <div>
                    <button className='input-btn' onClick={checkInput}>
                        guess
                    </button>
                </div>
            </div>
            <div className='result-panel'>{errMessage}</div>
        </div>
    );
}

export default GuessApp;
